/**
 * Module dependencies.
 */
var util = require('util')
  , OAuth2Strategy = require('passport-oauth2')
  , Profile = require('./profile')
  , InternalOAuthError = require('passport-oauth2').InternalOAuthError


/**
 * `Strategy` constructor.
 *
 * The Makerpass authentication strategy authenticates requests by delegating to
 * Makerpass using the OAuth 2.0 protocol.
 *
 * Applications must supply a `verify` callback which accepts an `accessToken`,
 * `refreshToken` and service-specific `profile`, and then calls the `done`
 * callback supplying a `user`, which should be set to `false` if the
 * credentials are not valid.  If an exception occured, `err` should be set.
 *
 * Options:
 *   - `clientID`      your Makerpass application's Client ID
 *   - `clientSecret`  your Makerpass application's Client Secret
 *   - `callbackURL`   URL to which Makerpass will redirect the user after granting authorization
 *   â€” `userAgent`     Optional User Agent string.
 *                     e.g: domain name of your application.
 *
 * Examples:
 *
 *     passport.use(new MakerpassStrategy({
 *         clientID: '123-456-789',
 *         clientSecret: 'shhh-its-a-secret'
 *         callbackURL: 'https://www.example.net/auth/makerpass/callback',
 *         userAgent: 'myapp.com'
 *       },
 *       function(accessToken, refreshToken, profile, done) {
 *         User.findOrCreate(..., function (err, user) {
 *           done(err, user)
 *         })
 *       }
 *     ))
 *
 * @param {Object} options
 * @param {Function} verify
 * @api public
 */
function Strategy(options, verify) {
  options = options || {}
  options.authorizationURL = options.authorizationURL || 'https://auth.makerpass.com/oauth/authorize'
  options.tokenURL = options.tokenURL || 'https://auth.makerpass.com/oauth/token'
  options.scopeSeparator = options.scopeSeparator || ','
  options.customHeaders = options.customHeaders || {}
  options.response_type = options.response_type || 'code'
  options.scope = options.scope || []

  if (!options.customHeaders['User-Agent']) {
    options.customHeaders['User-Agent'] = options.userAgent || 'passport-makerpass'
  }

  OAuth2Strategy.call(this, options, verify)
  this.name = 'makerpass'
  this._userProfileURL = options.userProfileURL || 'https://auth.makerpass.com/api/v1/me.json'
  this._oauth2.useAuthorizationHeaderforGET(true)
}

/**
 * Inherit from `OAuth2Strategy`.
 */
util.inherits(Strategy, OAuth2Strategy)


/**
 * Retrieve user profile from Makerpass.
 *
 * This function constructs a normalized profile, with the following properties:
 *
 *   - `provider`         always set to `makerpass`
 *   - `id`               the user's Makerpass ID
 *   - `username`         the user's Makerpass username
 *   - `displayName`      the user's full name
 *   - `profileUrl`       the URL of the profile for the user on Makerpass
 *   - `emails`           the user's email addresses
 *
 * @param {String} accessToken
 * @param {Function} done
 * @api protected
 */
Strategy.prototype.userProfile = function(accessToken, done) {
  this._oauth2.get(this._userProfileURL, accessToken, function (err, body, res) {
    var json

    if (err) {
      return done(new InternalOAuthError('Failed to fetch user profile', err))
    }

    try {
      json = JSON.parse(body)
    } catch (ex) {
      return done(new Error('Failed to parse user profile'))
    }

    var profile = Profile.parse(json)
    profile.provider  = 'makerpass'
    profile._raw = body
    profile._json = json

    done(null, profile)
  })
}


/**
 * Expose `Strategy`.
 */
module.exports = Strategy
